    "SharedDLLCounter": {
        "add": " SharedDLLCounter \u0027%_WindowsSystem%\\mydll.dll\u0027 /Inc:1\u003cbr\u003e",
        "example": "Upcount a SharedDLLCounter (+1):\u003cbr\u003eSharedDLLCounter \u0027%_WindowsSystem%\\qchain.exe\u0027 /Inc:1\u003cbr\u003eDecount a SharedDLLCounter (-2):\u003cbr\u003eSharedDLLCounter \u0027%_WindowsSystem%\\qchain.exe\u0027 /Dec:2",
        "description": "Creates / updates the registry entry counting the usage of shared components",
        "parameters": "\u003cbr\u003e\u003cbr\u003eFileName\tThe file name of the shared component that should be treated.\u003cbr\u003e\n/Inc\tIncreases the usage counter by 1.\u003cbr\u003e\n/Dec\tDecreases the usage counter by 1.\u003cbr\u003e\n/Inc:xx\tIncreases the usage counter by xx.\u003cbr\u003e\n/Dec:xx\tDecreases the usage counter by xx.\u003cbr\u003e\n/NoErrors\t\tThis switch causes Columbus to ignore any errors during setting the Shared Dll counter.\u003cbr\u003e",
        "syntax": "SharedDLLCounter FileName /Inc /Dec /Inc:xx /Dec:xx [/NoErrors]"
    }
